import React from 'react';
import {useContext} from 'react';
import SharedContext from './SharedContext';
import {List, ListItem, IconButton,
  ListItemText, Toolbar, ListItemButton, ListItemIcon} from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import StarIcon from '@mui/icons-material/Star';

const fetchMails = (setMails, mailbox) => {
  const user = JSON.parse(localStorage.getItem('user'));
  // const bearerToken = user.accessToken;
  const fetchStr = 'http://localhost:3010/v0/mail?mailbox='+ mailbox;
  fetch(fetchStr, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${user.accessToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      // setError('');
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
      // setError(`${error.status} - ${error.statusText}`);
    });
};

const fetchStar = (id) => {
  const user = JSON.parse(localStorage.getItem('user'));
  const fetchStr = 'http://localhost:3010/v0/star/'+ id;
  fetch(fetchStr, {
    method: 'post',
    headers: new Headers({
      'Authorization': `Bearer ${user.accessToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return true;
    })
    .catch((error) => {
      console.log(error);
    });
};


/**
 * @return {object} JSX Table
 */
function MailList() {
  const [mails, setMails] = React.useState([]);
  const {mailbox, setMailbox, setMailid,
    setDrawerO} = useContext(SharedContext);

  const clicked = (id) => {
    setMailbox('');
    setMailid(id);
    setMailV(true);
  };

  const clickedStar = (e, id) => {
    e.stopPropagation();
    fetchStar(id);
    window.location.reload(false);
  };

  React.useEffect(() => {
    fetchMails(setMails, mailbox);
  }, [mailbox]);

  return (
    <div onClick={() => setDrawerO(false)}
    sx={{flexGrow: 1, overflow: 'auto'}}>
      <Toolbar />
      <h2>{mailbox}</h2>
      <List>
        {mails.map((item) => (
          <ListItem>
            <ListItemButton onClick={() => clicked(item.id)} key={item.id}
              sx={{justifyContent: 'space-between'}}>
              <ListItemIcon>
                <PersonIcon />
              </ListItemIcon>
              <ListItem>
              <div style={{width: '75%'}}>
              <ListItemText primary={item.name} />
              <ListItemText primary={item.subject} />
              <ListItemText
                primaryTypographyProps={{style: {whiteSpace: 'nowrap',
                  overflow: 'hidden', textOverflow: 'ellipsis'}}}
                  primary={item.content} />
              </div>
              </ListItem>
              <div>
              <ListItemText align="right"
                primary={item.sent}/>
              <ListItemText align="right"
                primary={<br/>}/>
              <ListItemText align="right"
                primary={
                  <IconButton
                    onClick={(e) => clickedStar(e, item.id)}
                    role='button'
                    aria-label='starred'>
                      {(item.star == 0)&&<StarBorderIcon />}
                      {(item.star == 1)&&<StarIcon />}
                  </IconButton>}/>
              </div>
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </div>
  );
}

export default MailList;
