import SharedContext from './SharedContext';
import React from 'react';
import {AppBar, Toolbar, IconButton, Box, List, ListItem, ListItemButton,
  TextField, InputAdornment, ListItemText, ListItemIcon, Drawer,
  createTheme, Divider} from '@mui/material';
import DensityMediumIcon from '@mui/icons-material/DensityMedium';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import PersonIcon from '@mui/icons-material/Person';
import SearchIcon from '@mui/icons-material/Search';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import CancelIcon from '@mui/icons-material/Cancel';
import InboxIcon from '@mui/icons-material/Inbox';
import StarIcon from '@mui/icons-material/Star';
import ArrowCircleRightIcon from '@mui/icons-material/ArrowCircleRight';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import DraftsIcon from '@mui/icons-material/Drafts';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import AddIcon from '@mui/icons-material/Add';
import SettingsIcon from '@mui/icons-material/Settings';

const fetchMailbox = (setMB) => {
  const user = JSON.parse(localStorage.getItem('user'));
  fetch('http://localhost:3010/v0/mailbox', {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${user.accessToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setMB(json);
    })
    .catch((error) => {
      console.log(error);
      setMB([]);
    });
};

const fetchMail = (setMails, searchW) => {
  const user = JSON.parse(localStorage.getItem('user'));
  const str = encodeURIComponent(searchW);
  const fetchStr = 'http://localhost:3010/v0/mail?search='+ str;
  fetch(fetchStr, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${user.accessToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
    });
};

const drawerWidth = 200;

const theme = createTheme({
  breakpoints: {
    values: {
      xs: 0,
      sm: 550,
      md: 900,
      lg: 1200,
      xl: 1536,
    },
  },
});

const styles = (theme) => ({
  drawerOpen: {
    '& .MuiDrawer-paper': {
      width: drawerWidth,
      height: '100%',
      flexShrink: 0,
      boxSizing: 'border-box'},
  },
  drawerClose: {
    [theme.breakpoints.up('sm')]: {
      '& .MuiDrawer-paper': {
        width: drawerWidth,
        height: '100%',
        flexShrink: 0,
        boxSizing: 'border-box'},
    },
    [theme.breakpoints.down('sm')]: {
      overflowX: 'hidden',
    },
  },
});

/**
 * @return {object} JSX
 */
function TopBar() {
  const logout = () => {
    localStorage.removeItem('user');
    window.location.reload(false);
  };

  const [mails, setMails] = React.useState([]);
  const [searching, setSearching] = React.useState(false);
  const [searchW, setSearchW] = React.useState('');
  const {mailbox, setMailbox, drawerO,
    setMailid, setDrawerO} = React.useContext(SharedContext);
  const [currentMb, setCurrentMb] = React.useState('');
  const c = styles(theme);
  const [xsmdlg, setXS] = React.useState(((window.innerWidth>550)||
    (window.innerWidth===0))? false : true);

  const [mailboxes, setMB] = React.useState([]);

  const startSearching = () => {
    if (mailbox != '') {
      setCurrentMb(mailbox);
    }
    setMailbox('');
    setSearching(true);
  };

  React.useEffect(() => {
    fetchMail(setMails, searchW);
    fetchMailbox(setMB);
    const updateXS = () => {
      const x = ((window.innerWidth>550)||
        (window.innerWidth===0))? false : true;
      setXS(x);
    };
    window.addEventListener('resize', updateXS);
    return () => window.removeEventListener('resize', updateXS);
  }, [searchW]);

  const clicked = (id) => {
    setMailbox('');
    setMailid(id);
  };

  const exitSearching = () => {
    setSearching(false);
    setMailbox(currentMb);
    setSearchW('');
    console.log('Mailbox is', mailbox);
