            {desktop&&'CSE186 Mail - '}{desktop&&`${mailbox}`}
            <Box sx={{width: '70%'}}>
              <TextField
                margin="normal"
                id="search"
                name="search"
                size='small'
                fullWidth
                value={searchW}
                sx={{background: 'white'}}
                onClick={() => startSearching()}
                onInput={(event) => {
                  setSearchW(event.target.value);
