            </IconButton>
            <IconButton
              role='button'
              aria-label='logout'
              onClick={logout}>
              <PersonIcon />
            </IconButton>
            </div>}
            {searching&&<div><IconButton
              onClick={() => clearSearch()}
              role='button'
              aria-label='clearSearch'>
              <CancelIcon />
            </IconButton></div>}
          </Toolbar>
        </AppBar>
        <Drawer variant={xsmdlg? 'persistent':'permanent'}
    sx={drawerO? c.drawerOpen:c.drawerClose}
    open={drawerO}>
    <Box sx={{overflow: 'auto'}}>
      <List>
        <ListItem>
            CSE186 Mail
        </ListItem>
        <ListItemButton>
          <ListItemIcon>
            <InboxIcon />
          </ListItemIcon>
