        </ListItemButton>
        <Divider />
        <ListItemButton>
          <ListItemIcon>
            <StarIcon />
          </ListItemIcon>
          Starred
          <div>
            <ListItemText algin="right"
            primary={mailboxes.star}/>
          </div>
        </ListItemButton>
        <ListItemButton>
          <ListItemIcon>
            <ArrowCircleRightIcon />
          </ListItemIcon>
          Sent
        </ListItemButton>
        <ListItemButton>
          <ListItemIcon>
            <DraftsIcon />
          </ListItemIcon>
          Drafts
        </ListItemButton>
        <ListItemButton>
          <ListItemIcon>
            <DeleteOutlineIcon />
