import React from 'react';
import {useContext} from 'react';
import SharedContext from './SharedContext';
import {AppBar, Toolbar, IconButton, Box, List, ListItem, ListItemText,
  ListItemIcon} from '@mui/material';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import DownloadIcon from '@mui/icons-material/Download';
import DeleteIcon from '@mui/icons-material/Delete';
import PersonIcon from '@mui/icons-material/Person';


const fetchMail = (setMail, mailid) => {
  const user = JSON.parse(localStorage.getItem('user'));
  // const bearerToken = user.accessToken;
  const fetchStr = 'http://localhost:3010/v0/mail/'+ mailid;
  fetch(fetchStr, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${user.accessToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setMail(json);
    })
    .catch((error) => {
      console.log(error);
      setMail({});
    });
};

/**
 * @return {object} JSX
 */
function MailView() {
  const [mail, setMail] = React.useState({from: {name: '', email: ''}});
  const {mailid} = useContext(SharedContext);

  React.useEffect(() => {
    fetchMail(setMail, mailid);
  }, [mailid]);

  return (
    <Box>
      <AppBar sx={{background: 'white'}}>
        <Toolbar sx={{justifyContent: 'space-between'}}>
          <IconButton
            role='button'
            aria-label='go back'>
            <ArrowBackIosNewIcon />
          </IconButton>
          <div>
            <IconButton
              role='button'
              aria-label='unread'>
              <MailOutlineIcon />
            </IconButton>
            <IconButton
              role='button'
              aria-label='move'>
              <DownloadIcon />
            </IconButton>
            <IconButton
              role='button'
              aria-label='delete'>
              <DeleteIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>
    <Box>
      <Toolbar />
      <h1>{mail.subject}</h1>
      <h3>{mail.mailbox}</h3>
      <List>
        <ListItem>
          <ListItemIcon>
            <PersonIcon />
          </ListItemIcon>
          <div>
          <ListItemText>{mail.from.name}&emsp;&emsp;{mail.sent}</ ListItemText>
          <ListItemText>{mail.from.email}</ ListItemText>
          </div>
        </ListItem>
      </List>
    </Box>
    </Box>
  );
}

export default MailView;
