import React from 'react';
import {useContext} from 'react';
import SharedContext from './SharedContext';

const fetchMails = (setMails, setError, mailbox) => {
  const user = JSON.parse(localStorage.getItem('user'));
  const bearerToken = user.accessToken;
  const fetchStr = 'http://localhost:3010/v0/mail?mailbox='+(typeof mailbox);
  fetch(fetchStr, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setError('');
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
      setError(`${error.status} - ${error.statusText}`);
    });
};


/**
 * @return {object} JSX Table
 */
function MailList() {
  const user = JSON.parse(localStorage.getItem('user'));
  const [mails, setMails] = React.useState([]);
  const [name, setName] = React.useState(user ? user.name : '');
  const [error, setError] = React.useState('Logged Out');
  const [mailbox, setMailbox] = React.useState(useContext(SharedContext));

  const logout = () => {
    localStorage.removeItem('user');
    setMails([]);
    setName('');
    setError('Logged Out');
    setMailbox('inbox');
  };

  React.useEffect(() => {
    fetchMails(setMails, setError, mailbox);
  }, [mailbox]);

  return (
    <SharedContext.Consumer>
      {({mailbox}) => (
        <div>
        <button disabled={!name} onClick={logout}>Logout</button>
          <label>{name ? name : ''}</label>
          <p/>
          <h2>{mailbox}</h2>
          <table id='mails'>
            <thead>
              <tr>
                <th>Name</th>
                <th>Subject</th>
                <th>Content</th>
                <th>Received time</th>
              </tr>
            </thead>
            <tbody>
              <tr key={mails.id} id={'id'+mails.id}>
                <td>{mails.from}</td>
                <td>{mails.subject}</td>
                <td>{mails.content}</td>
                <td>{mails.received}</td>
              </tr>
              <tr key={'error'}>
                <td colSpan={4}>{error}</td>
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </SharedContext.Consumer>

  );
}

export default MailList;
