import React from 'react';
import {useContext} from 'react';
import SharedContext from './SharedContext';
import {AppBar, Toolbar, IconButton, Box, List, ListItem, ListItemText,
  ListItemIcon} from '@mui/material';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import DownloadIcon from '@mui/icons-material/Download';
import DeleteIcon from '@mui/icons-material/Delete';
import PersonIcon from '@mui/icons-material/Person';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import StarIcon from '@mui/icons-material/Star';

const fetchMail = (setMail, mailid) => {
  const user = JSON.parse(localStorage.getItem('user'));
  // const bearerToken = user.accessToken;
  const fetchStr = 'http://localhost:3010/v0/mail/'+ mailid;
  fetch(fetchStr, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${user.accessToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setMail(json);
    })
    .catch((error) => {
      console.log(error);
      setMail({});
    });
};

/**
 * @return {object} JSX
 */
function MailView() {
  const [mail, setMail] = React.useState({from: {name: '', email: ''}});
  const {mailid, setMailbox, setMailid} = useContext(SharedContext);

  React.useEffect(() => {
    fetchMail(setMail, mailid);
  }, [mailid]);

  const goBack = () => {
    setMailbox(mail.mailbox);
    setMailid('');
  };

  return (
    <Box>
      <AppBar sx={{background: 'white'}}>
        <Toolbar sx={{justifyContent: 'space-between'}}>
          <IconButton
            onClick={() => goBack()}
            role='button'
            aria-label='go back'>
            <ArrowBackIosNewIcon />
          </IconButton>
          <div>
            <IconButton
              role='button'
              aria-label='unread'>
              <MailOutlineIcon />
            </IconButton>
            <IconButton
              role='button'
              aria-label='move'>
              <DownloadIcon />
            </IconButton>
            <IconButton
              role='button'
              aria-label='delete'>
              <DeleteIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>
      <Box>
        <Toolbar />
        <h1>{mail.subject}</h1>
        <List>
          <ListItem sx={{justifyContent: 'space-between'}}>
            <ListItemText>{mail.mailbox}</ListItemText>
            <ListItemText align="right"
              primary={
                <IconButton
                  role='button'
                  aria-label='starred'>
                  <ArrowBackIcon />
                </IconButton>}/>
          </ListItem>
          <ListItem>
            <ListItem>
              <ListItemIcon>
                <PersonIcon />
              </ListItemIcon>
              <div sx={{justifyContent: 'start'}}>
          <ListItemText align="left">
            {mail.from.name}&emsp;&emsp;{mail.sent}</ ListItemText>
          <ListItemText align="left">{mail.from.email}</ ListItemText>
          </div>
          </ListItem>
          <div>
          <ListItemText align="right"
                primary={
                  <IconButton
                    role='button'
                    aria-label='starred'>
                      {(mail.star == 0)&&<StarBorderIcon />}
                      {(mail.star == 1)&&<StarIcon />}
                  </IconButton>}/>
          </div>
        </ListItem>
        <ListItem>
          {mail.content}
        </ListItem>
      </List>
    </Box>
    </Box>
  );
}

export default MailView;
