import React from 'react';
import {useContext} from 'react';
import SharedContext from './SharedContext';
import {List, ListItem, ListItemText} from '@mui/material';

const fetchMails = (setMails, mailbox) => {
  const user = JSON.parse(localStorage.getItem('user'));
  // const bearerToken = user.accessToken;
  const fetchStr = 'http://localhost:3010/v0/mail?mailbox='+ mailbox;
  fetch(fetchStr, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${user.accessToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      // setError('');
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
      // setError(`${error.status} - ${error.statusText}`);
    });
};


/**
 * @return {object} JSX Table
 */
function MailList() {
  const user = JSON.parse(localStorage.getItem('user'));
  const [mails, setMails] = React.useState([]);
  const [name, setName] = React.useState(user ? user.name : '');
  // const [error, setError] = React.useState('Logged Out');
  const {mailbox} = useContext(SharedContext);

  const logout = () => {
    localStorage.removeItem('user');
    setMails([]);
    setName('');
    // setError('Logged Out');
  };

  React.useEffect(() => {
    fetchMails(setMails, mailbox);
  }, [mailbox]);

  return (
    <div>
      <button disabled={!name} onClick={logout}>Logout</button>
      <label>{name ? name : ''}</label>
      <h2>{mailbox}</h2>
      <List>
        {mails.map((item) => (
          <ListItem key={item.id}>
            <ListItemText primary={item.mail.subject} />
          </ListItem>
        ))}
      </List>
    </div>
  );
}

export default MailList;
