import React from 'react';
import {useContext} from 'react';
import SharedContext from './SharedContext';
import {List, ListItem,
  ListItemText, Toolbar, ListItemButton, ListItemIcon} from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import StarBorderIcon from '@mui/icons-material/StarBorder';


const fetchMails = (setMails, mailbox) => {
  const user = JSON.parse(localStorage.getItem('user'));
  // const bearerToken = user.accessToken;
  const fetchStr = 'http://localhost:3010/v0/mail?mailbox='+ mailbox;
  fetch(fetchStr, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${user.accessToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      // setError('');
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
      // setError(`${error.status} - ${error.statusText}`);
    });
};


/**
 * @return {object} JSX Table
 */
function MailList() {
  const [mails, setMails] = React.useState([]);
  // const [error, setError] = React.useState('Logged Out');
  const {mailbox} = useContext(SharedContext);

  React.useEffect(() => {
    fetchMails(setMails, mailbox);
  }, [mailbox]);

  return (
    <div>
      <Toolbar />
      <h2>{mailbox}</h2>
      <List>
        {mails.map((item) => (
          <ListItem>
            <ListItemButton key={item.id}>
              <ListItemIcon>
                <PersonIcon />
              </ListItemIcon>
              <div style={{width: '75%'}}>
              <ListItemText primary={item.to.name} />
              <ListItemText primary={item.subject} />
              <ListItemText
                primaryTypographyProps={{style: {whiteSpace: 'nowrap',
                  overflow: 'hidden', textoverflow: 'ellipsis'}}}
                  primary={item.content} />
              </div>
              <div>
              <ListItemText align="right"
                primary={item.sent}/>
              <ListItemText align="right"
                primary={<StarBorderIcon />}/>
              </div>
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </div>
  );
}

export default MailList;
