import React from 'react';
import {useContext} from 'react';
import SharedContext from './SharedContext';
import {Drawer, List, ListItem, Box,
  ListItemText, ListItemButton} from '@mui/material';
// import StarBorderIcon from '@mui/icons-material/StarBorder';

const drawerWidth = 140;

const styles = (theme) => ({
  drawerOpen: {
    '& .MuiDrawer-paper': {
      position: 'relative',
      width: drawerWidth,
      flexShrink: 0,
      boxSizing: 'border-box'},
  },
  drawerClose: {
    [theme.breakpoints.up('sm')]: {
      '& .MuiDrawer-paper': {
        position: 'relative',
        width: drawerWidth,
        flexShrink: 0,
        boxSizing: 'border-box'},
    },
    [theme.breakpoints.down('sm')]: {
      overflowX: 'hidden',
    },
  },
});

/**
 * @return {object} JSX
 */
function NavBar() {
  const c = styles(theme);
  const {drawerO} = useContext(SharedContext);
  const [xsmdlg, setXS] = React.useState(((window.innerWidth>550)||
    (window.innerWidth===0))? false : true);
  useEffect(() => {
    const updateXS = () => {
      const x = ((window.innerWidth>550)||
        (window.innerWidth===0))? false : true;
      setXS(x);
    };
    window.addEventListener('resize', updateXS);
    return () => window.removeEventListener('resize', updateXS);
  }, []);
  return (
  <Drawer variant={xsmdlg? 'persistent':'permanent'}
    sx={drawerO? c.drawerOpen:c.drawerClose}
    open={drawerO}>
    <Box sx={{overflow: 'auto'}}>
      <List>
        <ListItem>
          <ListItemText>
            CSE186 Mail
          </ListItemText>
        </ListItem>
        <ListItemButton>
          Inbox
        </ListItemButton>
      </List>
    </Box>
  </Drawer>
  );
}

export default NavBar;
