import React from 'react';

import MailList from './MailList';
import MailView from './MailView';
import TopBar from './TopBar';
import Composing from './Composing';

import SharedContext from './SharedContext';

/**
 * @return {object} JSX
 */
function Home() {
  const [mailbox, setMailbox] = React.useState('Trash');
  const [mailid, setMailid] =
    React.useState('');
  const [drawerO, setDrawerO] = React.useState(false);
  const [comp, setComp] = React.useState(false);

  return (
    <SharedContext.Provider value={{mailbox, setMailbox, mailid,
      setMailid, drawerO, setDrawerO, comp, setComp}}>
      <TopBar />
      {comp&&<Composing />}
      {(mailbox!=='')&&<MailList />}
      {(mailid!=='')&&<MailView />}
    </SharedContext.Provider>
  );
}

export default Home;
