import React from 'react';
import SharedContext from './SharedContext';
import {AppBar, Toolbar, IconButton, Box, TextField} from '@mui/material';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBack';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

/**
 * @return {object} JSX
 */
function Composing() {
  const {setComp, setMailV, mailbox,
    setMailbox} = React.useContext(SharedContext);
  const [xsmdlg, setXS] = React.useState(((window.innerWidth>550)||
    (window.innerWidth===0))? false : true);
  const [currentMb, setCurrentMb] = React.useState('');

  const goBack = () => {
    console.log('Mailbox', mailbox);
    setComp(false);
    setMailV(true);
    setMailbox(currentMb);
  };

  React.useEffect(() => {
    if (mailbox != '') {
      setCurrentMb(mailbox);
    }
    setMailbox('');
    const updateXS = () => {
      const x = ((window.innerWidth>550)||
        (window.innerWidth===0))? false : true;
      setXS(x);
    };
    window.addEventListener('resize', updateXS);
    return () => window.removeEventListener('resize', updateXS);
  }, []);

  return (
    <Box sx={{paddingLeft: (xsmdlg?'0px':'200px')}}>
      <AppBar position='fixed' sx={{paddingLeft: (xsmdlg?'0px':'200px')}}>
        <Toolbar sx={{justifyContent: 'space-between'}}>
        <IconButton
          onClick={() => goBack()}
            role='button'
            aria-label='go back'>
            <ArrowBackIosNewIcon />
          </IconButton>
          <div>
            <IconButton
              role='button'
              aria-label='sent'>
              <ArrowForwardIcon />
            </IconButton>
          </div>
        </Toolbar>
        </AppBar>
        <Toolbar />
        <TextField
                margin="normal"
                id="to"
                name="to"
                size='small'
                fullWidth
                sx={{background: 'white'}}
              />
        <TextField
                margin="normal"
                id="subject"
                name="subject"
                size='small'
                fullWidth
                sx={{background: 'white'}}
              />
        <TextField
                margin="normal"
                id="content"
                name="content"
                fullWidth
                multiline
                sx={{background: 'white'}}
              />
    </Box>
  );
}

export default Composing;
