import SharedContext from './SharedContext';
import React from 'react';
// import {Navigate} from 'react-router-dom';
import {AppBar, Toolbar, IconButton, Box, List, ListItem, ListItemButton,
  TextField, InputAdornment, ListItemText, ListItemIcon} from '@mui/material';
import DensityMediumIcon from '@mui/icons-material/DensityMedium';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import PersonIcon from '@mui/icons-material/Person';
import SearchIcon from '@mui/icons-material/Search';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import CancelIcon from '@mui/icons-material/Cancel';

/*
const styles = {
  rightButtons: {
    float: 'right',
  },
};*/

const fetchMail = (setMails, searchW) => {
  const user = JSON.parse(localStorage.getItem('user'));
  // const bearerToken = user.accessToken;
  const str = encodeURIComponent(searchW);
  const fetchStr = 'http://localhost:3010/v0/mail?search='+ str;
  fetch(fetchStr, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${user.accessToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
    });
};

/**
 * @return {object} JSX
 */
function TopBar() {
  const logout = () => {
    localStorage.removeItem('user');
    window.location.reload(false);
  };

  /*
  const searching = (event) => {
    fetchMails
  }*/

  const [mails, setMails] = React.useState([]);
  const [searching, setSearching] = React.useState(false);
  const [searchW, setSearchW] = React.useState('');
  const {mailbox, setMailbox, setMailid} = React.useContext(SharedContext);
  const [currentMb, setCurrentMb] = React.useState('');

  const startSearching = () => {
    setCurrentMb(mailbox);
    setMailbox('');
    setSearching(true);
  };

  React.useEffect(() => {
    fetchMail(setMails, searchW);
  }, [searchW]);

  const clicked = (id) => {
    setMailbox('');
    setMailid(id);
  };

  const exitSearching = () => {
    setSearching(false);
    setMailbox(currentMb);
    setSearchW('');
    console.log('Mailbox is', mailbox);
  };

  const clearSearch = () => {
    setMails([]);
    setSearchW('');
  };

  const desktop = false;
  return (
    <Box>
        <AppBar position='fixed'>
          <Toolbar sx={{justifyContent: 'space-between'}}>
            {(!searching)&&<IconButton
              role='button'
              aria-label='toggle drawer'>
              <DensityMediumIcon />
            </IconButton>}
            {searching&&<IconButton
              onClick={() => exitSearching()}
              role='button'
              aria-label='exit searching'>
              <ArrowBackIosNewIcon />
            </IconButton>}
            {desktop&&'CSE186 Mail - '}{desktop&&`${mailbox}`}
            <Box sx={{width: '70%'}}>
              <TextField
                margin="normal"
                id="search"
                name="search"
                size='small'
                fullWidth
                value={searchW}
                sx={{background: 'white'}}
                onClick={() => startSearching()}
                onInput={(event) => {
                  setSearchW(event.target.value);
                }}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon />
                    </InputAdornment>
                  ),
                }}
              />
            </Box>
            {(!searching)&&<div>
            <IconButton
              role='button'
              aria-label='compose'>
              <MailOutlineIcon />
            </IconButton>
            <IconButton
              role='button'
              aria-label='logout'
              onClick={logout}>
              <PersonIcon />
            </IconButton>
            </div>}
            {searching&&<div><IconButton
              onClick={() => clearSearch()}
              role='button'
              aria-label='clearSearch'>
              <CancelIcon />
            </IconButton></div>}
          </Toolbar>
        </AppBar>
      {searching &&
      <div>
        <Toolbar />
        <List>
        {mails.map((item) => (
          <ListItem>
            <ListItemButton onClick={() => clicked(item.id)} key={item.id}
              sx={{justifyContent: 'space-between'}}>
              <ListItemIcon>
                <PersonIcon />
              </ListItemIcon>
              <div style={{width: '75%'}}>
              <ListItemText primary={item.name} />
              <ListItemText primary={item.subject} />
              <ListItemText
                primaryTypographyProps={{style: {whiteSpace: 'nowrap',
                  overflow: 'hidden', textOverflow: 'ellipsis'}}}
                  primary={item.content} />
              </div>
              <div>
              <ListItemText align="right"
                primary={item.sent}/>
              <ListItemText align="right"
                primary={<br/>}/>
              <ListItemText align="right"
                primary={
                  <IconButton
                    role='button'
                    aria-label='starred'>
                      <StarBorderIcon />
                  </IconButton>}/>
              </div>
            </ListItemButton>
          </ListItem>
        ))}
      </List>
      </div>}
      </Box>
  );
}

export default TopBar;
